-- Using SQL Server Enterprise Manager create a database named "Books"
-- Assign users to this database.  
-- It is recommended that a SQL Server user named "books" be created and be granted the "db_owner" role for the Books database.
-- Open Microsoft SQL Query Analyzer, connect to the Books database as the books user or as "sa" and execute the following script.

-- If a name other than "Books" is given to the database, change the line below to the appropriate name.
use Books
GO

-- DROP constraints and tables if they exist.

if exists (select * from dbo.sysobjects where id = object_id(N'[dbo].[FK_AUTHOR]') and OBJECTPROPERTY(id, N'IsForeignKey') = 1)
ALTER TABLE [dbo].[BOOK_AUTHORS] DROP CONSTRAINT FK_AUTHOR
GO

if exists (select * from dbo.sysobjects where id = object_id(N'[dbo].[FK_PUBLISHER]') and OBJECTPROPERTY(id, N'IsForeignKey') = 1)
ALTER TABLE [dbo].[BOOKS] DROP CONSTRAINT FK_PUBLISHER
GO

if exists (select * from dbo.sysobjects where id = object_id(N'[dbo].[FK_ISBN]') and OBJECTPROPERTY(id, N'IsForeignKey') = 1)
ALTER TABLE [dbo].[BOOK_AUTHORS] DROP CONSTRAINT FK_ISBN
GO

if exists (select * from dbo.sysobjects where id = object_id(N'[dbo].[BOOK_AUTHORS]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
drop table [dbo].[BOOK_AUTHORS]
GO

if exists (select * from dbo.sysobjects where id = object_id(N'[dbo].[BOOKS]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
drop table [dbo].[BOOKS]
GO

if exists (select * from dbo.sysobjects where id = object_id(N'[dbo].[AUTHORS]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
drop table [dbo].[AUTHORS]
GO

if exists (select * from dbo.sysobjects where id = object_id(N'[dbo].[PUBLISHERS]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
drop table [dbo].[PUBLISHERS]
GO

-- Create tables

CREATE TABLE [dbo].[AUTHORS] (
	[AUTHOR_ID] [int] NOT NULL ,
	[FIRSTNAME] [varchar] (255) COLLATE SQL_Latin1_General_CP1_CI_AS NULL ,
	[LASTNAME] [varchar] (255) COLLATE SQL_Latin1_General_CP1_CI_AS NULL ,
	[MIDDLEINIT] [varchar] (255) COLLATE SQL_Latin1_General_CP1_CI_AS NULL 
) ON [PRIMARY]
GO

CREATE TABLE [dbo].[BOOKS] (
	[ISBN] [varchar] (255) COLLATE SQL_Latin1_General_CP1_CI_AS NOT NULL ,
	[TITLE] [varchar] (255) COLLATE SQL_Latin1_General_CP1_CI_AS NULL ,
	[SUBTITLE] [varchar] (255) COLLATE SQL_Latin1_General_CP1_CI_AS NULL ,
	[PUBLISHER] [int] NULL ,
	[PUBLISH_YEAR] [int] NULL ,
	[EDITION] [int] NULL ,
	[TYPE] [varchar] (255) COLLATE SQL_Latin1_General_CP1_CI_AS NULL 
) ON [PRIMARY]
GO

CREATE TABLE [dbo].[BOOK_AUTHORS] (
	[ISBN] [varchar] (255) COLLATE SQL_Latin1_General_CP1_CI_AS NOT NULL ,
	[AUTHOR_ID] [int] NOT NULL 
) ON [PRIMARY]
GO

CREATE TABLE [dbo].[PUBLISHERS] (
	[PUBLISHER_ID] [int] NOT NULL ,
	[NAME] [varchar] (255) COLLATE SQL_Latin1_General_CP1_CI_AS NULL ,
	[LOCATION] [varchar] (255) COLLATE SQL_Latin1_General_CP1_CI_AS NULL 
) ON [PRIMARY]
GO

